export default function RegisterComponent() {
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({});
  const [loading, setLoading] = useState(false);

  const register = async () => {
    try {
      setLoading(true);

      // Check if username or email contains cusswords
      if (containsCusswords(credentials.name) || containsCusswords(credentials.email)) {
        throw new Error("Your username or email contains inappropriate words. Please choose a different username or email.");
      }
  
      let res = await RegisterAPI(credentials.email, credentials.password);
      const user = {
        id: res.user.uid,
        userID: res.user.uid,
        name: credentials.name,
        email: credentials.email,
        imageLink: "https://firebasestorage.googleapis.com/v0/b/socialmedia-d3d44.appspot.com/o/profileImages%2Fsave.png?alt=media&token=02fcb347-a834-4b7a-8a69-9394865eebe1",
      };
  
      const emailRegex = /\.(com|org|edu)$/i;
      if (!emailRegex.test(credentials.email)) {
        throw new Error("Invalid email format. Please use a valid email ending with .com, .org, or .edu.");
      }
      
      await postUserData(user);

      // Store user email and ID in localStorage
      localStorage.setItem("userEmail", res.user.email);
      localStorage.setItem("userID", res.user.uid);
  
      toast.success("Account Created!");
      
      // Navigate to home page after successful registration
      navigate("/home");
      
    } catch (err) {
      console.error(err);
      toast.error("Cannot Create your Account");
    } finally {
      setLoading(false);
    }
  };

  // Function to check if a string contains cusswords
  const containsCusswords = (text) => {
    const lowerCaseText = text.toLowerCase();
    return cusswords.some((cussword) => lowerCaseText.includes(cussword));
  };

  return (
    <div className="login-wrapper">
      <img src={LinkedinLogo} className="linkedinLogo" />

      <div className="login-wrapper-inner">
        <h1 className="heading">Make the most of college experience!</h1>

        <div className="auth-inputs">
          <input
            onChange={(event) =>
              setCredentials({ ...credentials, name: event.target.value })
            }
            type="text"
            className="common-input"
            placeholder="Your Name"
          />
          <input
            onChange={(event) =>
              setCredentials({ ...credentials, email: event.target.value })
            }
            type="email"
            className="common-input"
            placeholder="Email or phone number"
          />
          <input
            onChange={(event) =>
              setCredentials({ ...credentials, password: event.target.value })
            }
            type="password"
            className="common-input"
            placeholder="Password (6 or more characters)"
          />
        </div>
        <button onClick={register} className="login-btn" disabled={loading}>
          {loading ? "Registering..." : "Agree & Join"}
        </button>
      </div>
      <hr className="hr-text" data-content="or" />
      <div className="google-btn-container">
        <p className="go-to-signup">
          Already on Hive?{' '}
          <span className="join-now" onClick={() => navigate("/")}>
            Sign in
          </span>
        </p>
      </div>
    </div>
  );
}
